<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');include_once(APPPATH."controllers/parentController.php");class Agents extends ParentController {    //public variables...        public function __construct()    {        parent::__construct();    }    /* The default function that gets called when visiting the page */    public function index()    {        $target_function = $this->intelligent_router_model->get_last_saved_route_for_current_controller();        if($target_function != 'index')        {            //setting section            $this->bodyData['section'] = $target_function;            //and there we go...            $this->$target_function();        }else{            if($this->bodyData['section'] == 'index')            {                $this->bodyData['section'] = 'customers';            }            $this->customers();        }    }    public function customers()    {        $headerData['title'] = 'Customers';        $this->bodyData['someMessage'] = '';                if(isset($_POST['addAgent'])){            if($this->form_validation->run('add_agent') == true){                if( $this->agents_model->insert() == true){                    $this->bodyData['someMessage'] = array('message'=>'Agent Added Successfully!', 'type'=>'alert-success');                }else{                    $this->bodyData['someMessage'] = array('message'=>'Some Unknown database fault happened. please try again a few moments later. Or you can contact your system provider.<br>Thank You', 'type'=>'alert-warning');                }            }        }        $this->bodyData['agents'] = $this->agents_model->customers();        $this->load->view('components/header', $headerData);        $this->load->view('agents/welcome', $this->bodyData);        $this->load->view('components/footer');    }    public  function suppliers()    {        $headerData['title'] = 'Suppliers';        $this->bodyData['someMessage'] = '';        if(isset($_POST['addAgent'])){            if($this->form_validation->run('add_agent') == true){                if( $this->agents_model->insert() == true){                    $this->bodyData['someMessage'] = array('message'=>'Agent Added Successfully!', 'type'=>'alert-success');                }else{                    $this->bodyData['someMessage'] = array('message'=>'Some Unknown database fault happened. please try again a few moments later. Or you can contact your system provider.<br>Thank You', 'type'=>'alert-warning');                }            }        }        $this->bodyData['agents'] = $this->agents_model->suppliers();        $this->load->view('components/header', $headerData);        $this->load->view('agents/welcome', $this->bodyData);        $this->load->view('components/footer');    }    public function _check_unique_agent($agent)    {        $name = $this->input->post('name');        $type = $this->input->post('type');        $this->db->select("id");        $this->db->where(array(            'name'=>$name,            'type'=>$type,        ));        $num = $this->db->get('agents')->num_rows();        if($num > 0)        {            $this->form_validation->set_message('_check_unique_agent','Agent already exist. Please try again');            return false;        }        return true;    }}/* End of file welcome.php *//* Location: ./application/controllers/welcome.php */