<?php                                            if($record->related_other_agent != 0){                                                echo $record->other_agent_name;                                                echo "<span style='color: gray'> ( other agent )</span>";                                            }else if($record->related_customer != 0){                                                echo $record->customer_name;                                                echo "<span style='color: gray'> ( customer )</span>";                                            }else if($record->related_contractor != 0){                                                echo $record->contractor_name;                                                echo "<span style='color: gray'> ( contractor )</span>";                                            }else{                                                echo "Self";                                            }                                            ?>                                        </td>                                        <td>                                            <?php                                            if($record->balance >= 0){                                                $type = 'Dr: ';                                                $color = 'darkgreen';                                                $balance = $record->balance;                                            }else{                                                $type = 'Cr: ';                                                $color = 'rgba(255,0,0,1)';                                                $balance = ($record->balance* -1);                                            }                                            ?>                                            <span style="color: <?= $color; ?>"><?= $type."".$this->helper_model->money($balance) ?></span>                                        </td>                                    </tr>                                <?php                                }                                ?>                                </tbody>                            </table>                        </div>                    </div>                </div>            </div>            <div class="col-md-4">                <div class="panel panel-default">                    <div class="panel-heading">                        <h3 class="panel-title"><i class="fa fa-user fa-fw"></i> Account Summery</h3>                    </div>                    <?php                    //below we are calculating equity, assets and Liabilities                    $equity = array();                    $assets_liabilities = array();                    foreach($balance_sheet as $record){                        if($record->ac_type == "owner_equity" || $record->ac_type == "retain_earning" || $record->ac_type == "dividend"){                            array_unshift($equity,$record);                        }else{                            array_unshift($assets_liabilities, $record);                        }                    }                    $assets = array();                    $liabilities = array();                    foreach($assets_liabilities as $record){                        if($record->debit == 0 || $record->debit == ''){                            array_unshift($liabilities,$record);                        }else{                            array_unshift($assets, $record);                        }                    }                    /////////////////////////////////////////////////////////////////////////                    ?>                    <?php                    $total_assets = 0;                    $total_liabilities = 0;                    ?>                    <?php  foreach($assets as $record): ?>                        <?php $total_assets += $record->debit; ?>                    <?php endforeach; ?>                    <?php $total_assets+= ($customer_balance['cash'] + $customer_balance['receivable']) ?>                    <?php  foreach($liabilities as $record): ?>                        <?php $total_liabilities += $record->credit; ?>                    <?php endforeach; ?>                    <?php                    $total_credit = 0;                    $total_debit = 0;                    ?>                    <?php  foreach($equity as $record): ?>                        <?php $total_credit += $record->credit; ?>                        <?php $total_debit += $record->debit; ?>                    <?php endforeach; ?>                    <?php                    $total_equity = $total_credit - $total_debit + $net_profit;                    ?>                    <!--/////////////////////////////////////////////////////////////////////////////////////////////////////-->                    <div class="panel-body">                        <div class="list-group">                            <table class="table table-hover table-striped" style="font-size:12px;">                                <tbody>                                <tr>                                    <td>                                        Total Assets                                    </td>                                    <td>                                        <?php                                        if($total_assets >= 0){                                            $type = 'Dr: ';                                            $color = 'darkgreen';                                            $balance = $total_assets;                                        }else{                                            $type = 'Cr: ';                                            $color = 'rgba(255,0,0,1)';                                            $balance = ($total_assets * -1);                                        }                                        ?>                                        <span style="color: <?= $color; ?>"><?= $type."".$this->helper_model->money($balance) ?></span>                                    </td>                                </tr>                                <tr>                                    <td>                                        Total Liabilities                                    </td>                                    <td>                                        <?php                                        if($total_liabilities >= 0){                                            $type = 'Dr: ';                                            $color = 'darkgreen';                                            $balance = $total_liabilities;                                        }else{                                            $type = 'Cr: ';                                            $color = 'rgba(255,0,0,1)';                                            $balance = ($total_liabilities);                                        }                                        ?>                                        <span style="color: <?= $color; ?>"><?= $type."".$this->helper_model->money($balance) ?></span>                                    </td>                                </tr>                                <tr>                                    <td>                                        Total Equity                                    </td>                                    <td>                                        <?php                                        if($total_equity >= 0){                                            $type = 'Dr: ';                                            $color = 'darkgreen';                                            $balance = $total_equity;                                        }else{                                            $type = 'Cr: ';                                            $color = 'rgba(255,0,0,1)';                                            $balance = ($total_equity);                                        }                                        ?>                                        <span style="color: <?= $color; ?>"><?= $type."".$this->helper_model->money($balance) ?></span>                                    </td>                                </tr>                                <tr>                                    <td>                                        Total Income                                    </td>                                    <td>                                        <?php                                        if($total_revenue >= 0){                                            $type = 'Dr: ';                                            $color = 'darkgreen';                                            $balance = $total_revenue;                                        }else{                                            $type = 'Cr: ';                                            $color = 'rgba(255,0,0,1)';                                            $balance = ($total_revenue);                                        }                                        ?>                                        <span style="color: <?= $color; ?>"><?= $type."".$this->helper_model->money($balance+$customer_balance['cash'] + $customer_balance['receivable']) ?></span>                                    </td>                                </tr>                                <tr>                                    <td>                                        Total Expenses                                    </td>                                    <td>                                        <?php                                        if($total_expense >= 0){                                            $type = 'Dr: ';                                            $color = 'darkgreen';                                            $balance = $total_expense;                                        }else{                                            $type = 'Cr: ';                                            $color = 'rgba(255,0,0,1)';                                            $balance = ($total_expense);                                        }                                        ?>                                        <span style="color: <?= $color; ?>"><?= $type."".$this->helper_model->money($balance) ?></span>                                    </td>                                </tr>                                </tbody>                            </table>                        </div>                    </div>                </div>            </div>            </div>        </div>    </div></div><script src="<?= js()?>sorttable.js"></script></body></html>