<html><head>    <title>Journal <?= $account_holder ?></title>    <link href="<?= css()?>bootstrap.min.css" rel="stylesheet"></head><body><style>    table{        font-size: 12px;    }    table{        font-size: 12px;    }    .search-table2 input, .search-table2 select{        width:100%;    }    .search-table2 input, .search-table2 select{        height: 30px;    }    .multiple_entites{        border-bottom: 1px dashed lightgray;    }</style><div id="page-wrapper" style="min-height: 700px;">    <div class="container-fluid">        <div class="row">            <div class="col-lg-12 text-center" style="font-weight: bold;">                <?= $account_holder."" ?><br>                Journal<br>            </div>            <div class="col-lg-12">                <table class="table table-bordered table-hover table-striped" style="font-size:12px;">                    <thead style="background-color: ; color: ;">                    <tr>                        <th style="width: 5%;">Voucher#</th>                        <th style="width: 10%;">Date</th>                        <th style="width: 55%">Transaction Detail</th>                        <th style="width: 11%;">Debit</th>                        <th style="width: 11%;">Credit</th>                    </tr>                    </thead>                    <tbody>                    <?php                    $total_debit = 0;                    $total_credit = 0;                    ?>                    <?php  foreach($journal as $voucher): ?>                        <?php                        $voucher_date = $this->carbon->createFromFormat('Y-m-d', $voucher->voucher_date)->toFormattedDateString();                        $voucher_debit = 0;                        $voucher_credit = 0;                        $count = 0;                        $entries = sizeof($voucher->entries);                        ?>                        <?php foreach($voucher->entries as $voucher_entry): ?>                            <?php                            $count++;                            $total_credit += $voucher_entry->credit;                            $voucher_credit+= $voucher_entry->credit;                            $total_debit += $voucher_entry->debit;                            $voucher_debit += $voucher_entry->debit;                            ?>                            <tr style="border-top: <?= ($count == 1)?'5':'0'; ?>px solid lightblue;">                                <?php if($count == 1){echo "<td rowspan=".($entries+1).">".$voucher->voucher_id."</td>";} ?>                                <?php if($count == 1){echo "<td rowspan=".($entries+1).">$voucher_date</td>";} ?>                                <td>                                    <?= $voucher_entry->title; ?> | <?= $voucher_entry->ac_type; ?>                                    <?php if($voucher_entry->related_agent_name != ''): ?>                                        | <?= $voucher_entry->related_agent; ?>: <?= $voucher_entry->related_agent_name; ?>                                    <?php endif ?>                                    <?php if($voucher_entry->description != ''): ?>                                        | (<?= $voucher_entry->description; ?>)                                    <?php endif ?>                                </td>                                <td><?= (($voucher_entry->dr_cr == 'debit')?$voucher_entry->debit:''); ?></td>                                <td><?= (($voucher_entry->dr_cr == 'credit')?$voucher_entry->credit:''); ?></td>                            </tr>                        <?php endforeach ?>                        <tr style="background-color: <?= ($voucher_debit == $voucher_credit)?'':''; ?>;">                            <td colspan="" style="text-align: center; color: gray; font-style: italic;">                                <div style="width: 60%; min-height: 10px; float: left; border: 0px solid red;">                                    <?= $voucher->voucher_details; ?>                                </div>                                <div style="width: 20%; float: left; border: 0px solid red;">                                    <div class="text-center" style="color: #555555;">                                        <?php                                        if($voucher->trip_id > 0){                                            echo " Trip# ".$voucher->trip_id;                                        }                                        ?>                                    </div>                                    <div class="text-center" style="color: #555555;">                                        <?php                                        if($voucher->tanker_number != ''){                                            echo "Tanker# ".$voucher->tanker_number;                                        }                                        ?>                                    </div>                                </div>                                <div class="text-right" style="width: 20%; float: left; border: 0px solid red;">                                    <strong style="color: blue;">Total</strong>                                </div>                            </td>                            <?php                            $balance = $voucher_debit-$voucher_credit;                            ?>                            <th style="color: blue; background-color: <?= (($balance != 0)?"rgba(255,0,0,0.1)":"rgba(0,255,0,0.1)") ?>"> <?= $voucher_debit ?> </th>                            <th style="color: blue; background-color: <?= (($balance != 0)?"rgba(255,0,0,0.1)":"rgba(0,255,0,0.1)") ?>"><?= $voucher_credit ?></th>                        </tr>                    <?php                    endforeach;                    ?>                    </tbody>                </table>            </div>        </div>    </div></div><script src="<?= js()?>sorttable.js"></script></body></html>