<style>    .white-popup {        position: relative;        background: #FFF;        padding: 20px;        width: auto;        max-width: 1000px;        margin: 20px auto;    }</style><style>    #voucher_area select{        font-size: 12px;    }</style><script>    function fetch_agents(agent, callback){        var agents_area = document.getElementById("agents_area");        $.get("<?=base_url()."accounts/fetch_agents/"?>"+agent,function(data,status){            agents_area.innerHTML= data;            callback();        });    }    function agent_type_changed()    {        var agent_type_selected_index = document.getElementById("agent_type").selectedIndex;        var agent_type = document.getElementById("agent_type").options[agent_type_selected_index].value;        if(agent_type != ''){            document.getElementById('agents_area').innerHTML = '<span style="font-style: italic; color: gray;">Loading...</span>';            fetch_agents(agent_type,function(){            });        }    }    function get_tanker_expense_voucher()    {        var url = ""        var trip_id = document.getElementById('trip_id').value;        trip_id = (trip_id == '')?'none':trip_id;        var expense_date = document.getElementById('expense_date').value;        expense_date = (expense_date == '')?'none':expense_date;        var other_info = document.getElementById('other_info').value;        other_info = (other_info == '')?'none':other_info;        var amount = document.getElementById('amount').value;        amount = (amount == '')?'none':amount;        var expense_title_selected_index = document.getElementById("expense_title").selectedIndex;        var expense_title = document.getElementById("expense_title").options[expense_title_selected_index].value;        expense_title = (expense_title == '')?'none':expense_title;        var agent_type_selected_index = document.getElementById("agent_type").selectedIndex;        var agent_type = document.getElementById("agent_type").options[agent_type_selected_index].value;        agent_type = (agent_type == '')?'none':agent_type;        var agent_id = 'none'        if(agent_type != 'none'){            var agent_id_selected_index = document.getElementById("agent_id").selectedIndex;            var agent_id = document.getElementById("agent_id").options[agent_id_selected_index].value;        }        var account_type_selected_index = document.getElementById("account_type").selectedIndex;        var account_type = document.getElementById("account_type").options[account_type_selected_index].value;        //preparing the url        url = "<?= base_url()."accounts/tanker_expense_voucher/" ?>"+trip_id+"/"+expense_date+"/"+other_info+"/"+expense_title+"/"+agent_type+"/"+agent_id+"/"+amount+"/"+account_type;        //setting the url        var test_link = document.getElementById("get_tanker_expense_voucher_link");        test_link.setAttribute('href', url);        test_link.className+=" mfp-close";    }</script><div id="page-wrapper" style="min-height: 700px;"><div class="container-fluid"><div class="row">    <div class="col-lg-12">        <section class="col-md-4">            <h3 class="page-header">                Accounts <small></small>            </h3>        </section>        <section class="col-md-8">            <ul id="myTab" class="nav nav-pills page-header">                <li class="active"><a href="<?= base_url()."accounts/global_test_accounts/"?>">Global Accounts</a></li>                <li class=""><a href="<?= base_url()."accounts/customers/"?>">Customers</a></li>                <li class=""><a href="<?= base_url()."accounts/contractors/"?>">Contractors</a></li>                <li class=""><a href="<?= base_url()."accounts/companies/"?>">Companies</a></li>                <li class=""><a href="<?= base_url()."accounts/other_agents/"?>">Other Agents</a></li>            </ul>        </section>    </div></div><div class="row"><div class="col-lg-12"><div class="panel-body"><div class="col-lg-12">    <div class="table-responsive">        <div class="white-popup" style="max-width: 500px;">            <div id="voucher_area">                <script>                </script>                <div class="row" style="">                    <div class="col-lg-12 text-center" style="color: #269abc">                        <h3>Global Accounts</h3><hr>                    </div>                    <div class="col-md-12 center-block">                       <form method="get" action="<?= base_url()."accounts/get_global_balance_sheet" ?>" target="_blank">                           <div class="col-sm-12 form-group" style="margin-top: 5px;">                               <label class="col-md-4 control-label">From: </label>                               <div class="col-lg-8">                                   <input class="form-control" id="from_date" type="date" value="" name="from">                               </div>                           </div>                           <div class="col-sm-12 form-group" style="margin-top: 5px;">                               <label class="col-md-4 control-label">To: </label>                               <div class="col-lg-8">                                   <input class="form-control" id="to_date" type="date" value="" name="to">                               </div>                           </div>                        <div class="col-sm-12 form-group" style="margin-top: 5px;">                            <label class="col-md-4 control-label">Related Agent</label>                            <div class="col-md-4">                                <select class="form-control" id="agent_type" name="agent_type" onchange="agent_type_changed()">                                    <option value="">None</option>                                    <option value="other_agents">Other Agent</option>    