p->trip_related_details as $trip_detail){                        $service_charges = round($trip_detail->contractor_benefits(), 3);                        $contractor_service_charges_credit_amount = $trip_detail->get_total_contractor_service_charges_credit_amount();                        $total_remaining += ($service_charges - $contractor_service_charges_credit_amount);                    }                }            }            $total_remaining = round($total_remaining, 3);            ?>            <form method="post" on onsubmit="return check_payment()">                <!--Hidden fields-->                <input type="hidden" value="<?= $trip_ids_str ?>" name="trip_ids">                <input type="hidden" value="<?= $total_remaining ?>" name="payable" id="payable">                <input type="hidden" name="voucher_type" value="<?= $voucher_type ?>">                <input type="hidden" value="<?= $form_id ?>" name="form_resubmission">                <!--******************-->                <div class="row" style="">                    <div class="col-lg-12 text-center" style="color: #269abc">                        <h3>Mass Credit <?= (($voucher_type == 1)?"( Commission )":"( Service Charges)") ?></h3><hr>                    </div>                    <div class="col-md-12 center-block">                        <form method="get" action="">                            <div class="col-sm-12 form-group" style="margin-top: 5px;">                                <label class="col-md-4 control-label">Date: </label>                                <div class="col-lg-8">                                    <input class="form-control" required="required" id="voucher_date" type="date" value="" name="voucher_date">                                </div>                            </div>                            <div class="col-sm-12 form-group" style="margin-top: 5px;">                                <label class="col-md-4 control-label">Total Amount (Credit): </label>                                <div class="col-lg-8">                                    <?= $this->helper_model->money($total_remaining); ?>                                </div>                            </div>                            <div class="col-sm-12 form-group" style="margin-top: 5px;">                                <label class="col-md-4 control-label">Related Agent</label>                                <div class="col-md-4">                                    <select class="form-control" id="agent_type" name="agent_type" onchange="agent_type_changed()">                                        <option value="">None</option>                                        <option value="other_agents">Other Agent</option>                                        <option value="customers">Customer</option>                                        <option value="carriage_contractors">Contractor</option>                                        <option value="companies">Company</option>                                    </select>                                </div>                                <div class="col-md-4" id="agents_area">                                </div>                            </div>                            <div class="col-sm-12 form-group" style="margin-top: 5px;">                                <label class="col-md-4 control-label">Other Info:  </label>                                <div class="col-lg-8">                                    <textarea name="voucher_details" class="form-control"></textarea>                                </div>                            </div>                            <div class="col-sm-12 form-group" style="margin-top: 5px;">                                <label class="col-md-4 control-label"></label>                                <div class="col-lg-8">                                    <input type="submit" name="add_mass_credit" value="Confirm" cla