<html><head>    <title>Accounts</title>    <link href="<?= css()?>bootstrap.min.css" rel="stylesheet"></head><body><style>    table{        font-size: 11px;    }    .multiple_entites{       border-bottom: 1px dashed lightgray;    }</style><div id="page-wrapper" style="min-height: 700px;">    <div class="container-fluid">        <div class="row">            <div class="col-lg-12">                <section class="col-md-12">                    <h3 class="">                       Company Accounts: <?= $profile->name; ?><small></small>                    </h3>                </section>            </div>        </div>        <div class="row">            <div class="col-lg-12">                <div class="panel-body">                    <div id="myTabContent" class="tab-content" style="min-height: 500px;">                        <div class="tab-pane fade in active" id="customers">                            <div class="table-responsive">                                <table class="table table-bordered table-hover table-striped accounts-table sortable" style="">                                    <thead style="border-top: 4px solid lightgray;">                                    <tr>                                        <?= ((in_array('id', $columns) == true)?"<th>Id</th>":"") ?>                                        <?= ((in_array('trip_type', $columns) == true)?"<th>Trip Type</th>":"") ?>                                        <?= ((in_array('trip_date', $columns) == true)?"<th>Trip Date</th>":"") ?>                                        <?= ((in_array('tanker', $columns) == true)?"<th>Tanker</th>":"") ?>                                        <?= ((in_array('product', $columns) == true)?"<th>Product</th>":"") ?>                                        <?= ((in_array('route', $columns) == true)?"<th>Route</th>":"") ?>                                        <?= ((in_array('company_freight_unit', $columns) == true)?"<th style='width: 5%;'>Freight/Unit (cmp)</th>":"") ?>                                        <?= ((in_array('total_freight_for_company', $columns) == true)?"<th style='width: 7%;'>Total Freight (cmp)</th>":"") ?>                                        <?= ((in_array('wht', $columns) == true)?"<th>W.H.T</th>":"") ?>                                        <?= ((in_array('company_commission', $columns) == true)?"<th style='width: 7%;'>Company's Commission</th>":"") ?>                                        <?= ((in_array('company_commission_paid', $columns) == true)?"<th style='width: 5%;'>Paid (cmp)</th>":"") ?>                                        <?= ((in_array('company_commission_remaining', $columns) == true)?"<th style='width: 5%;'>Remaining (cmp)</th>":"") ?>                                        <?= ((in_array('contractor', $columns) == true)?"<th>Contractor</th>":"") ?>                                        <?= ((in_array('contractor_freight', $columns) == true)?"<th style='width: 7%;'>Contractor's Freight</th>":"") ?>                                        <?= ((in_array('contractor_net_freight', $columns) == true)?"<th style='width: 7%;'>Contractor Net Freight</th>":"") ?>                                        <?= ((in_array('contractor_freight_paid', $columns) == true)?"<th style='width: 5%;'>Paid (cnt)</th>":"") ?>                                        <?= ((in_array('contractor_freight_remaining', $columns) == true)?"<th style='width: 5%;'>Remaining (cnt)</th>":"") ?>                                    </tr>                                    </thead>                                    <tbody>                                    <?php                                    $grand_total_freight_for_company = 0;                                    $total_wht = 0;                                    $total_company_commission_amount = 0;                                    $total_paid_to_company = 0;                                    $total_company_remaining = 0;                                    $total_contractor_freight_amount = 0;                                    $total_contractor_net_freight_amount = 0;                                    $total_paid_to_contractor = 0;                                    $total_contractor_remaining = 0;                                    ?>                                    <?php $parent_count = 0; ?>                                    <?php  foreach($accounts as $trip): ?>                                        <?php                                        $count = 0;                                        $num_trip_product_details = sizeof($trip->trip_related_details);                                        ?>                                        <?php foreach($trip->trip_related_details as $detail): ?>                                            <?php                                            $count++;                                            $parent_count++;                                            ?>                                            <tr style="border-top: <?= ($count == 1)?'3':'0'; ?>px solid lightblue;">                                                <!--first column-->                                                <?php if(in_array('id', $columns) == true): ?>                                                    <?php if($count == 1){echo "<td rowspan=".($num_trip_product_details).">".$trip->trip_id."</td>";} ?>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('trip_type', $columns) == true): ?>                                                    <?php if($count == 1){echo "<td rowspan=".($num_trip_product_details).">".(($trip->type == '2')?'Local':'')."</td>";} ?>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('trip_date', $columns) == true): ?>                                                    <?php if($count == 1){echo "<td rowspan=".($num_trip_product_details).">".Carbon::createFromFormat('Y-m-d',$trip->dates->entry_date)->toFormattedDateString()."</td>";} ?>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('tanker', $columns) == true): ?>                                                    <?php if($count == 1){echo "<td rowspan=".($num_trip_product_details).">".$trip->tanker->tanker_number."</td>";} ?>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('product', $columns) == true): ?>                                                    <td> <?= $detail->product->name; ?> </td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('route', $columns) == true): ?>                                                    <td><?= $detail->source->name." To ".$detail->destination->name; ?></td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('company_freight_unit', $columns) == true): ?>                                                    <td><?= $detail->company_freight_unit; ?></td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('total_freight_for_company', $columns) == true): ?>                                                    <td>                                                        <?php                                                        $total_freight_for_company = $detail->get_total_freight_for_company();                                                        $grand_total_freight_for_company += $total_freight_for_company;                                                        echo $this->helper_model->money($total_freight_for_company);                                                        ?>                                                    </td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('wht', $columns) == true): ?>                                                    <td>                                                        <?php                                                        $wht = $trip->company->wht;                                                        $wht_amount = $detail->get_wht_amount($trip->company->wht);                                                        $total_wht += $wht_amount;                                                        ?>                                                        <?= $wht."% = ".$this->helper_model->money($wht_amount); ?>                                                    </td>                                                <?php endif; ?>                                                <!--additional computation-->                                                <?php                                                $company_commission_amount = round($detail->get_company_commission_amount($trip->company->commission_1), 3);                                                $paid_to_company = round($detail->get_paid_to_company(), 3);                                                $company_remaining = round($company_commission_amount - $paid_to_company ,4);                                                $total_company_commission_amount += $company_commission_amount;                                                $total_paid_to_company += $paid_to_company;                                                $total_company_remaining += $company_remaining;                                                ?>                                                <!--first column-->                                                <?php if(in_array('company_commission', $columns) == true): ?>                                                    <td><?= $trip->company->commission_1."% = ".$this->helper_model->money($company_commission_amount); ?></td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('company_commission_paid', $columns) == true): ?>                                                    <td class="<?= (($company_remaining != 0)?'unpaid':'paid') ?>">                                                        <?= $this->helper_model->money($paid_to_company); ?>                                                    </td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('company_commission_remaining', $columns) == true): ?>                                                    <td><?= $this->helper_model->money($company_remaining); ?></td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('contractor', $columns) == true): ?>                                                    <?php if($count == 1){echo "<td rowspan=".($num_trip_product_details).">".$trip->contractor->name."</td>";} ?>                                                <?php endif; ?>                                                <!--additional computation-->                                                <!--additional computation-->                                                <?php                                                $contractor_freight_amount = round($detail->get_contractor_freight_amount_according_to_company($trip->get_contractor_freight_according_to_company()),3);                                                $contractor_net_freight_amount = $contractor_freight_amount - $company_commission_amount;                                                $paid_to_contractor = round($detail->get_paid_to_contractor(),3);                                                $contractor_remaining = round(($contractor_freight_amount - $paid_to_contractor),4);                                                $contractor_commission = $trip->contractor->commission_1 - $trip->company->wht - $trip->company->commission_1;                                                $contractor_commission_amount = $detail->get_contractor_commission_amount($contractor_commission);                                                $total_contractor_freight_amount += $contractor_freight_amount;                                                $total_contractor_net_freight_amount += $contractor_net_freight_amount;                                                $total_paid_to_contractor += $paid_to_contractor;                                                $total_contractor_remaining += $contractor_remaining;                                                ?>                                                <!--first column-->                                                <?php if(in_array('contractor_freight', $columns) == true): ?>                                                    <td><?= $trip->get_contractor_freight_according_to_company()."% = ".$this->helper_model->money($contractor_freight_amount); ?></td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('contractor_net_freight', $columns) == true): ?>                                                    <td><?= ($trip->get_contractor_freight_according_to_company() - $trip->company->commission_1)."% = ".$this->helper_model->money($contractor_net_freight_amount); ?></td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('contractor_freight_paid', $columns) == true): ?>                                                    <td class="<?= (($contractor_remaining != 0)?'unpaid':'paid') ?>">                                                        <?= $paid_to_contractor; ?>                                                    </td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('contractor_freight_remaining', $columns) == true): ?>                                                    <td><?= $this->helper_model->money($contractor_remaining); ?></td>                                                <?php endif; ?>                                            </tr>                                        <?php endforeach ?>                                    <?php endforeach; ?>                                    </tbody>                                    <tfoot>                                    <tr>                                        <?= ((in_array('id', $columns) == true)?"<th>Totals</th>":"") ?>                                        <?= ((in_array('trip_type', $columns) == true)?"<th></th>":"") ?>                                        <?= ((in_array('trip_date', $columns) == true)?"<th></th>":"") ?>                                        <?= ((in_array('tanker', $columns) == true)?"<th></th>":"") ?>                                        <?= ((in_array('product', $columns) == true)?"<th></th>":"") ?>                                        <?= ((in_array('route', $columns) == true)?"<th></th>":"") ?>                                        <?= ((in_array('company_freight_unit', $columns) == true)?"<th></th>":"") ?>                                        <?= ((in_array('total_freight_for_company', $columns) == true)?"<th style='text-align: center'>".$this->helper_model->money($grand_total_freight_for_company)."</th>":"") ?>                                        <?= ((in_array('wht', $columns) == true)?"<th>".$this->helper_model->money($total_wht)."</th>":"") ?>                                        <?= ((in_array('company_commission', $columns) == true)?"<th>".$this->helper_model->money($total_company_commission_amount)."</th>":"") ?>                                        <?= ((in_array('company_commission_paid', $columns) == true)?"<th>".$this->helper_model->money($total_paid_to_company)."</th>":"") ?>                                        <?= ((in_array('company_commission_remaining', $columns) == true)?"<th>".$this->helper_model->money($total_company_remaining)."</th>":"") ?>                                        <?= ((in_array('contractor', $columns) == true)?"<th></th>":"") ?>                                        <?= ((in_array('contractor_freight', $columns) == true)?"<th>".$this->helper_model->money($total_contractor_freight_amount)."</th>":"") ?>                                        <?= ((in_array('contractor_net_freight', $columns) == true)?"<th style='width: 7%;'>".$this->helper_model->money($total_contractor_net_freight_amount)."</th>":"") ?>                                        <?= ((in_array('contractor_freight_paid', $columns) == true)?"<th>".$this->helper_model->money($total_paid_to_contractor)."</th>":"") ?>                                        <?= ((in_array('contractor_freight_remaining', $columns) == true)?"<th>".$this->helper_model->money($total_contractor_remaining)."</th>":"") ?>                                    </tr>                                    </tfoot>                                </table>                            </div>                        </div>                    </div>                </div>            </div>        </div>    </div></div></body></html>