<script>    function show_monthly_results(month, path){        var path = path+month;        window.location.href = path;    }    function mass_payment(){        var check_boxes = document.getElementsByClassName("filter_check_box");        var trip_ids = '0';        for(var count = 0; count < check_boxes.length; count++){            if(check_boxes[count].checked == true){                trip_ids = trip_ids+"_"+check_boxes[count].value;            }        }        //preparing the url        var url = "<?= base_url()."customers/customer_mass_payment/" ?>"+trip_ids;        //setting the url        var mass_payment_link = document.getElementById("mass_payment_link");        mass_payment_link.setAttribute('href', url);    }    function mass_credit(){        var check_boxes = document.getElementsByClassName("filter_check_box");        var trip_ids = '0';        for(var count = 0; count < check_boxes.length; count++){            if(check_boxes[count].checked == true){                trip_ids = trip_ids+"_"+check_boxes[count].value;            }        }        //preparing the url        var url = "<?= base_url()."customers/customer_mass_credit/" ?>"+trip_ids;        //setting the url        var mass_credit_link = "";        mass_credit_link = document.getElementById("freight_mass_credit_link");        mass_credit_link.setAttribute('href', url);    }</script><style>    .white-popup {        position: relative;        background: #FFF;        padding: 20px;        width: auto;        max-width: 500px;        margin: 20px auto;    }    .benefits-details-popup {        position: relative;        background: #FFF;        padding: 20px;        width: auto;        max-width: 500px;        margin: 20px auto;    }    .custom-accounts-popup{        position: relative;        background-color: #FFF;        padding: 20px;        width: auto;        max-width: 600px;        margin: 20px auto;    }    .customer_credi