mer = round($detail->get_paid_to_customer(),3);                                                $customer_remaining = round(($customer_freight_amount - $paid_to_customer), 3);                                                $total_freight_for_customer = $detail->get_total_freight_for_customer();                                                $grand_total_freight_for_customer += $total_freight_for_customer;                                                $total_customer_freight_amount += $customer_freight_amount;                                                $total_paid_to_customer += $paid_to_customer;                                                $total_customer_remaining += $customer_remaining;                                                ?>                                                <td><?= $trip->contractor->name; ?></td>                                                <td><?= $detail->customer_freight_unit; ?></td>                                                <td><?= $detail->get_total_freight_for_customer(); ?></td>                                                <td><?= $trip->customer->freight."% = ".$this->helper_model->money($customer_freight_amount); ?></td>                                                <td class="<?= (($customer_remaining != 0)?'unpaid':'paid') ?>">                                                    <div style="border-bottom: 1px solid lightgray;">                                                        <a href="<?= base_url()."customers/add_customer_payment/".$detail->product_detail_id."/".$profile->id."/".$customer_freight_amount."/".$customer_remaining ?>" class="add_customer_payment_link" style="display: block; width: 100%; min-height: 20px;">                                                            <?= $this->helper_model->money($paid_to_customer); ?>                                                        </a>                                                    </div>                                        