<div class="white-popup">    <script>        function check_payment()        {            var payable = document.getElementById("payable").value;            if(payable == 0){                return false;            }            return true;        }        function fetch_agents(agent, callback){            var agents_area = document.getElementById("agents_area");            $.get("<?=base_url()."accounts/fetch_agents/"?>"+agent,function(data,status){                agents_area.innerHTML= data;                callback();            });        }        function agent_type_changed()        {            var agent_type_selected_index = document.getElementById("agent_type").selectedIndex;            var agent_type = document.getElementById("agent_type").options[agent_type_selected_index].value;            if(agent_type != ''){                document.getElementById('agents_area').innerHTML = '<span style="font-style: italic; color: gray;">Loading...</span>';                fetch_agents(agent_type,function(){                });            }        }    </script>    <div class="row">        <div class="col-lg-12">            <?php            $total_remaining = 0;            foreach($trips as $trip){                foreach($trip->trip_related_details as $trip_detail){                    $customer_freight_amount = round($trip_detail->get_customer_freight_amount($trip->customer->freight), 3);                    $customer_freight_credit_amount = $trip_detail->get_total_customer_freight_credit_amount();                    $total_remaining += ($customer_freight_amount - $customer_freight_credit_amount);                }            }            $total_remaining = round($total_remaining, 3);            ?>            <form method="post" on onsubmit="return check_payment()">                <!--Hidden fields-->                <input type="hidden" value="<?= $trip_ids_str ?>" name="trip_ids">                <input type="hidden" value="<?= $total_remaining ?>" name="payable" id="payable">                <input type="hidden" name="voucher_type" value="<?= $voucher_type ?>">                <input type="hidden" value="<?= $form_id ?>" name="form_resubmission">                <!--******************-->                <div class="row" style="">                    <div class="col-lg-12 text-center" style="color: #269abc">                        <h3>Mass Credit Freight</h3><hr>                    </div>                    <div class="col-md-12 center-block">                        <form method="post">                            <div class="col-sm-12 form-group" style="margin-top: 5px;">                                <label class="col-md-4 control-label">Date: </label>                                <div class="col-lg-8">                                    <input class="form-control" required="required" id="voucher_date" type="date" value="" name="voucher_date">                                </div>                            </div>                            <div class="col-sm-12 form-group" style="margin-top: 5px;">                                <label class="col-md-4 control-label">Total Amount (Credit): </label>                                <div class="col-lg-8">                                    <?= $this->helper_model->money($total_remaining); ?>                                </div>                            </div>                            <div class="col-sm-12 form-group" style="margin-top: 5px;">                                <label class="col-md-4 control-label">Related Agent</label>                                <div class="col-md-4">                                    <select class="form-control" id="agent_type" name="agent_type" onchange="agent_type_changed()">                                        <option value="">None</option>                                        <option value="other_agents">Other Agent</option>                                        <option value="customers">Customer</option>                                        <option value="carriage_contractors">Contractor</option>                                        <option value="companies">Company</option>                                    </select>                                </div>                                <div class="col-md-4" id="agents_area">                                </div>                            </div>                            <div class="col-sm-12 form-group" style="margin-top: 5px;">                                <label class="col-md-4 control-label">Other Info:  </label>                                <div class="col-lg-8">                                    <textarea name="voucher_details" class="form-control"></textarea>                                </div>                            </div>                            <div class="col-sm-12 form-group" style="margin-top: 5px;">                                <label class="col-md-4 control-label"></label>                                <div class="col-lg-8">                                    <input type="submit" name="add_mass_credit" value="Confirm" class="btn btn-success center-block" style="width: 100%;">                                </div>                            </div>                        </form>                    </div>                </div>            </form>        </div>    </div></div>