<html><head>    <title>Trips</title>    <link href="<?= css()?>bootstrap.min.css" rel="stylesheet"></head><body><style>    table{        font-size: 12px;    }</style><div id="page-wrapper" style="min-height: 700px;">    <div class="container-fluid">        <div class="row">            <div class="col-lg-12">                <section class="col-md-12">                    <h1>                     Driver: <?= $profile->name; ?><small>  Trips  (<?= $month;?>) </small>                    </h1>                </section>            </div>        </div>        <div class="row">            <div class="table-responsive">                <table class="table table-bordered table-hover table-striped sortable">                    <thead style="border-top: 4px solid lightgray;">                    <tr>                        <?= ((in_array('id', $columns) == true)?"<th>ID</th>":"") ?>                        <?= ((in_array('route', $columns) == true)?"<th>Route</th>":"") ?>                        <?= ((in_array('entry_date', $columns) == true)?"<th>Entry Date</th>":"") ?>                        <?= ((in_array('filling_date', $columns) == true)?"<th>Filling Date</th>":"") ?>                        <?= ((in_array('decanding_date', $columns) == true)?"<th>Decanding Date</th>":"") ?>                    </tr>                    </thead>                    <tbody>                    <?php                    foreach($trips as $trip){                        $entry_date = $this->carbon->createFromFormat('Y-m-d',$trip->entry_date)->toFormattedDateString();                        $filling_date = ($trip->filling_date != '0000-00-00')?$this->carbon->createFromFormat('Y-m-d',$trip->filling_date)->toFormattedDateString():"--";                        $decanding_date = ($trip->decanding_date != '0000-00-00')?$this->carbon->createFromFormat('Y-m-d',$trip->decanding_date)->toFormattedDateString():"--";;                        ?>                        <tr>                            <?= ((in_array('id', $columns) == true)?"<td>$trip->trip_id</td>":"") ?>                            <?= ((in_array('route', $columns) == true)?"<td>$trip->route</td>":"") ?>                            <?= ((in_array('entry_date', $columns) == true)?"<td>$entry_date</td>":"") ?>                            <?= ((in_array('filling_date', $columns) == true)?"<td>$filling_date</td>":"") ?>                            <?= ((in_array('decanding_date', $columns) == true)?"<td>$decanding_date</td>":"") ?>                        </tr>                    <?php                    }                    ?>                    </tbody>                </table>            </div>        </div>    </div></div><script src="<?= js()?>sorttable.js"></script></body></html>