yle="min-height: 500px;">                        <div class="tab-pane fade in active" id="trips">                            <div class="table-responsive">                                <table>                                    <tr>                                        <td colspan="8"><input style="border: 0px; font-weight: bold;" type="month" value="<?= $month ?>" onchange="show_monthly_results(this.value,'<?= base_url()."drivers/trips_expenses/".$profile->id."/";?>')"> </td>                                    </tr>                                </table>                                <form name="selection_form" id="selection_form" method="post" action="<?php                                if(strpos($this->helper_model->page_url(),'?') == false){                                    echo $this->helper_model->page_url()."?";                                }else{echo $this->helper_model->page_url()."&";}                                ?>print">                                <table class="table table-bordered table-hover table-striped sortable">                                    <thead style="border-top: 4px solid lightgray;">                                    <tr>                                        <th></th>                                        <th><div><input id="" type="checkbox" name="column[]" value="id" style="" checked></div></th>                                        <th><div><input id="" type="checkbox" name="column[]" value="route" style="" checked></div></th>                                        <th><div><input id="" type="checkbox" name="column[]" value="expense_date" style="" checked></div></th>                                        <th><div><input id="" type="checkbox" name="column[]" value="expense_title" style="" checked></div></th>                                        <th><div><input id="" type="checkbox" name="column[]" value="expense_amount" style="" checked></div> </th>                              