           array_unshift($assets_liabilities, $record);                    }                }                $assets = array();                $liabilities = array();                foreach($assets_liabilities as $record){                    if($record->debit == 0 || $record->debit == ''){                        array_unshift($liabilities,$record);                    }else{                        array_unshift($assets, $record);                    }                }                /////////////////////////////////////////////////////////////////////////                ?>                <?php                $total_assets = 0;                $total_liabilities = 0;                ?>                <?php  foreach($assets as $record): ?>                    <?php $total_assets += $record->debit; ?>                <?php endforeach; ?>                <?php $total_assets+= ($customer_balance['cash'] + $customer_balance['receivable']) ?>                <!--Calculating the liabilities-->                <?php  foreach($liabilities as $record): ?>                    <?php $total_liabilities += $record->credit; ?>                <?php endforeach; ?>                <?php                $total_liabilities += $customer_balance['shortage_expense_liability_at_destination'];                $total_liabilities += $customer_balance['shortage_expense_liability_after_decanding'];                ?>                <!--*******************************************************-->                <?php                $total_credit = 0;                $total_debit = 0;                ?>                <?php  foreach($equity as $record): ?>                    <?php $total_credit += $record->credit; ?>                    <?php $total_debit += $record->debit; ?>                <?php endforeach; ?>                <?php                    $total_equity = $total_credit - $total_debit + $net_profit;                ?>                <!--/////////////////////////////////////////////////////////////////////////////////////////////////////-->                <div class="panel-body">                    <div class="list-group">                        <table class="table table-hover table-striped" style="font-size:12px;">                            <tbody>                            <tr>                                <td>                                    Total Assets                                </td>                                <td>                                    <?php                                    if($total_assets >= 0){                                        $type = 'Dr: ';                                        $color = 'darkgreen';                                        $balance = $total_assets;                                    }else{                                        $type = 'Dr: ';                                        $color = 'rgba(255,0,0,1)';                                        $balance = ($total_assets * -1);                                    }                                    ?>                                    <span style="color: <?= $color; ?>"><?= $type."".$this->helper_model->money($balance) ?></span>                                </td>                            </tr>                            <tr>                                <td>                                   Total Liabilities                                </td>                                <td>                                    <?php                                    if($total_liabilities >= 0){                                        $type = 'Cr: ';                                        $color = 'darkgreen';                                        $balance = $total_liabilities;                                    }else{                                        $type = 'Cr: ';                                        $color = 'rgba(255,0,0,1)';                                        $balance = ($total_liabilities);                                    }                                    ?>                                    <span style="color: <?= $color; ?>"><?= $type."".$this->helper_model->money($balance) ?></span>                                </td>                            </tr>                            <tr>                                <td>                                    Total Equity                                </td>                                <td>                                    <?php                                    if($total_equity >= 0){                                        $type = 'Cr: ';                                        $color = 'darkgreen';                                        $balance = $total_equity;                                    }else{                                        $type = 'Cr: ';                                        $color = 'rgba(255,0,0,1)';                                        $balance = ($total_equity);                                    }                                    ?>                                    <span style="color: <?= $color; ?>"><?= $type."".$this->helper_model->money($balance) ?></span>                                </td>                            </tr>                            <tr>                                <td>                                    Total Income                                </td>                                <td>                                    <?php                                    if($total_revenue >= 0){                                        $type = 'Cr: ';                                        $color = 'darkgreen';                                        $balance = $total_revenue;                                    }else{                                        $type = 'Cr: ';                                        $color = 'rgba(255,0,0,1)';                                        $balance = ($total_revenue);                                    }                                    ?>                                    <span style="color: <?= $color; ?>"><?= $type."".$this->helper_model->money($balance+$customer_balance['cash'] + $customer_balance['receivable']) ?></span>                                </td>                            </tr>                            <tr>                                <td>                                    Total Expenses                                </td>                                <td>                                    <?php                                    if($total_expense >= 0){                                        $type = 'Dr: ';                                        $color = 'darkgreen';                                        $balance = $total_expense;                                    }else{                                        $type = 'Cr: ';                                        $color = 'rgba(255,0,0,1)';                                        $balance = ($total_expense);                                    }                                    ?>                                    <span style="color: <?= $color; ?>"><?= $type."".$this->helper_model->money($balance) ?></span>                                </td>                            </tr>                            </tbody>                        </table>                    </div>                </div>            </div>        </div>      </form>    </div></div></div><script src="<?= js()."jquery.magnific-popup.min.js"; ?>"></script><script>    $('.tanker_expense_voucher_edit').magnificPopup({        type: 'ajax',        showCloseBtn:false    });    $('.tanker_expense_voucher').magnificPopup({        type: 'ajax',        showCloseBtn:false    });    $('.open-custom-journal-popup').magnificPopup({        type: 'inline',        showCloseBtn:true    });</script>