<html>    <head>        <style>            table{                font-size: 11px;            }        </style>        <link rel="stylesheet" href="<?= css()."bootstrap.min.css" ?>">    </head><body><?php    $balance = ($opening_balance == '')?0: $opening_balance;?><div class="row">    <div class="col-lg-8 center-block" style="position: absolute; margin: auto; right: 0px; left: 0px;">        <div style="text-align: center">            <?php            switch($agent_type)            {                case "other_agents":                    $account_holder_type = "Other Agent";                    break;                case "customers":                    $account_holder_type = "Customer";                    break;                case "contractors":                    $account_holder_type = "Carriage Contractor";                    break;                case "companies":                    $account_holder_type = "Companies";                    break;            }            ?>            <h3>Balance Sheet</h3>            <?= $account_holder_type.": ".ucwords($account_holder) ?><br>            <?= (($from_date != "")?Carbon::createFromFormat('Y-m-d',$from_date)->toFormattedDateString():'Start')." To ".(($to_date != "")?Carbon::createFromFormat('Y-m-d',$to_date)->toFormattedDateString():'End') ?>        </div>        <table class="table">            <thead>            <tr>                <th colspan="6" style="text-align: right;">                    <i>Opening Balance: <?= (($balance < 0)?"(".($this->helper_model->money($balance*-1)).")":$this->helper_model->money($balance)) ?></i>                </th>            </tr>            <tr style="background-color: lightskyblue;">                <th>Voucher Id</th><th style="width: 15%;">Voucher Date</th><th>Description</th><th>Debit</th><th>Credit</th><th>Balance</th>            </tr>            </thead>            <tbody>            <?php foreach($records as $record): ?>                <tr>                    <td><?= $record->journal_voucher_id ?></td>                    <td><?= Carbon::createFromFormat('Y-m-d',$record->voucher_date)->toFormattedDateString(); ?></td>                    <td>                        <?php                        echo $record->title." ".$record->ac_type;                        ?>                        <span style="color:gray; font-style: italic;"><?= $record->description ?></span>                    </td>                    <td><?= (($record->dr_cr ==1)?$record->debit_amount:'') ?></td>                    <td><?= (($record->dr_cr ==0)?$record->credit_amount:'') ?></td>                    <td>                        <?php                        $balance = (($record->debit_amount - $record->credit_amount) + $balance)                        ?>                        <?= (($balance < 0)?"(".($this->helper_model->money($balance*-1)).")":$this->helper_model->money($balance)) ?>                    </td>                </tr>            <?php endforeach; ?>            </tbody>        </table>    </div></div></body></html>