<div class="white-popup">    <script>        /*function delete_record(table, id, callback){            $.get("delete/function/url/"+table+"/"+id,function(data,status){                callback(data);            });        }        function want_to_remove(id){            delete_record('company_accounts',id, function(response){                //removing the element                var elem = document.getElementById('record_'+id);                elem.parentNode.removeChild(elem);            });        }*/        function remove_entry(entry_id)        {            document.getElementById("delete_entry_id").value = entry_id;            document.getElementById('previous_entries_form').submit();        }        function validate_payment_form()        {           var amount = document.getElementById("amount").value;            if(amount == 0){                return false;            }            return true;        }        ///**********overriding the close behaviour of magnificpopup***********//        $.magnificPopup.instance.close = function () {            //here you can do what ever you want to do before closing the popup            $.magnificPopup.proto.close.call(this);        };        ///************************************************///    </script>    <div class="row">        <div class="col-lg-12">            <form action="" method="post" onsubmit="return validate_payment_form()">                <div class="col-sm-5" style="margin-top: 5px;">                    <input type="hidden" name="form_id" value="<?= $form_id; ?>">                    <input type="hidden" name="customer_id" value="<?= $customer_id; ?>">                    <input type="hidden" name="trip_detail_id" value="<?= $trip_detail_id; ?>">                    <input type="hidden" name="actual_freight" value="<?= $actual_freight; ?>">                    <label>Payment Date:</label>                    <input class="form-control" required="required" type="date" name="payment_date" value="<?= $this->carbon->now(new DateTimeZone('Asia/Karachi'))->toDateString(); ?>">                </div>                <div class="col-sm-5" style="margin-top: 5px;">                    <label>Freight:</label>                    <input class="form-control" required="required" type="number" step="any" min="0.001" <?= (($remaining == 0)?'disabled':'') ?> max="<?= $remaining ?>" name="amount" value="<?= $remaining ?>" id="amount" placeholder="enter amount" >                </div>                <div class="col-sm-2" style="margin-top: 5px;">                    <label></label>                    <input type="submit" name="add_customer_payment" style="width: 100%;" class="btn btn-success" value="Add">                </div>            </form>        </div>    </div>    <div class="row" style="margin-top: 10px;">        <div class="col-lg-12">            <form method="post" id="previous_entries_form">                <input type="hidden" id="delete_entry_id" name="entry_id">                <table class="table table-bordered">                    <tr>                        <th>Payment Date</th>                        <th>Amount</th>                        <th></th>                    </tr>                    <?php                    $total_paid = 0;                    foreach($previous_payments as $previous_payment){                        $total_paid+= $previous_payment->amount;                        ?>                        <tr id="record_<?= $previous_payment->id; ?>">                            <td><?= Carbon::createFromFormat('Y-m-d',$previous_payment->payment_date)->toFormattedDateString(); ?></td>                            <td><?= $this->helper_model->money($previous_payment->amount) ?></td>                            <td><a href="#" style="color: red;" onclick="remove_entry(<?= $previous_payment->id ?>)"><i class="fa fa-minus-circle"></i> remove</a></td>                        </tr>                    <?php                    }                    ?>                    <tfoot>                    <tr>                        <th colspan="3" style="text-align: center; font-style: italic">Total Paid: <?= $this->helper_model->money($total_paid) ?></th>                    </tr>                    </tfoot>                </table>            </form>        </div>    </div></div>