<html><head>    <title>Expenses</title>    <link href="<?= css()?>bootstrap.min.css" rel="stylesheet"></head><body><style>    table{        font-size: 12px;    }</style><div id="page-wrapper" style="min-height: 700px;">    <div class="container-fluid">        <div class="row">            <div class="col-lg-12">                <section class="col-md-12">                    <h1>                      <?= $profile->truck_number; ?>  Other Expenses <small>   Other Expenses (<?= $month;?>) </small>                    </h1>                </section>            </div>        </div>        <div class="row">            <div class="table-responsive">                <table class="table table-bordered table-hover table-striped sortable">                    <thead style="border-top: 4px solid lightgray;">                    <tr>                        <?= ((in_array('expense_date', $columns) == true)?"<th>Expense Date</th>":"") ?>                        <?= ((in_array('expense_title', $columns) == true)?"<th>Expense Title</th>":"") ?>                        <?= ((in_array('expense_amount', $columns) == true)?"<th>Expense Amount</th>":"") ?>                    </tr>                    </thead>                    <tbody>                    <?php                    $total_expense = 0;                    foreach($expenses as $expense){                        $total_expense+=$expense->amount;                        $expense_date = $this->carbon->createFromFormat('Y-m-d', $expense->expense_date)->toFormattedDateString();;                        $expense_amount = $this->helper_model->money($expense->amount);                        ?>                        <tr>                            <?= ((in_array('expense_date', $columns) == true)?"<td>$expense_date </td>":"") ?>                            <?= ((in_array('expense_title', $columns) == true)?"<td>$expense->description</td>":"") ?>                            <?= ((in_array('expense_amount', $columns) == true)?"<td>$expense_amount</td>":"") ?>                        </tr>                    <?php                    }                    ?>                    </tbody>                    <tfoot>                    <tr>                        <?php                            $total_expense = $this->helper_model->money($total_expense);                        ?>                        <?= ((in_array('expense_date', $columns) == true)?"<th> </th>":"") ?>                        <?= ((in_array('expense_title', $columns) == true)?"<th></th>":"") ?>                        <?= ((in_array('expense_amount', $columns) == true)?"<th>$total_expense</th>":"") ?>                    </tr>                    </tfoot>                </table>            </div>        </div>    </div></div><script src="<?= js()?>sorttable.js"></script></body></html>