<html><head>    <title>Trips</title>    <link href="<?= css()?>bootstrap.min.css" rel="stylesheet"></head><body><style>    table{        font-size: 12px;    }</style><div id="page-wrapper" style="min-height: 700px;">    <div class="container-fluid">        <div class="row">            <div class="col-lg-12">                <section class="col-md-12">                    <h1>                      <?= $profile->truck_number; ?><small>  Trips</small>                    </h1>                </section>            </div>        </div>        <div class="row">            <div class="table-responsive">                <table class="table table-bordered table-hover table-striped trips-table sortable">                    <thead style="border-top: 4px solid lightgray;">                    <tr>                        <?= ((in_array('id', $columns) == true)?"<th>Id</th>":"") ?>                        <?= ((in_array('route', $columns) == true)?"<th>Route</th>":"") ?>                        <?= ((in_array('customer', $columns) == true)?"<th>Customer</th>":"") ?>                        <?= ((in_array('contractor', $columns) == true)?"<th>Contractor</th>":"") ?>                        <?= ((in_array('company', $columns) == true)?"<th>Company</th>":"") ?>                        <?= ((in_array('trip_date', $columns) == true)?"<th>Trip Date</th>":"") ?>                    </tr>                    </thead>                    <tbody>                    <?php                    foreach($trips as $trip){                        $entry_date = $this->carbon->createFromFormat('Y-m-d', $trip->entry_date)->toFormattedDateString();                        ?>                        <tr>                            <?= ((in_array('id', $columns) == true)?"<td> $trip->trip_id </td>":"") ?>                            <?= ((in_array('route', $columns) == true)?"<td> $trip->route </td>":"") ?>                            <?= ((in_array('customer', $columns) == true)?"<td> $trip->customerName </td>":"") ?>                            <?= ((in_array('contractor', $columns) == true)?"<td> $trip->contractorName </td>":"") ?>                            <?= ((in_array('company', $columns) == true)?"<td> $trip->companyName </td>":"") ?>                            <?= ((in_array('trip_date', $columns) == true)?"<td>$entry_date</td>":"") ?>                        </tr>                    <?php                    }                    ?>                    </tbody>                </table>            </div>        </div>    </div></div><script src="<?= js()?>sorttable.js"></script></body></html>